query findUser($search: String!) {
  users(
    filter: {
      or: [
        { email: { includesInsensitive: $search } }
        { firstName: { includesInsensitive: $search } }
        { lastName: { includesInsensitive: $search } }
        { username: { includesInsensitive: $search } }
      ]
    }
  ) {
    nodes {
      email
      firstName
      lastName
      username
      id
    }
  }
}

query findOrg($search: String!) {
  organisations(filter: { name: { includesInsensitive: $search } }) {
    nodes {
      name
      id
    }
  }
}

